<?phpsession_start();if (isset($_POST['cart_remove'])){   $item_name = $_POST['item_name'];   foreach($_SESSION['cart'] as $key => $value){       if ($value['item_name'] === $item_name ){           unset($_SESSION['cart'][$key]);           $_SESSION['cart'] = array_values($_SESSION['cart']);           echo "<script> alert('Item Removed'); window.location.href ='shoping-cart.php' </script>";       }   }}