<?phpsession_start();$qty = $_POST['quantity'];if (isset($_POST['update'])){    $item_name = $_POST['item_name'];    foreach($_SESSION['cart'] as $key => $value){        if ($value['item_name'] === $item_name ){            $_SESSION['cart'][$key]['quantity']=$qty;            echo "<script> alert('Item Updated'); window.location.href ='shoping-cart.php' </script>";        }    }}