<?phpsession_start();if (isset($_POST['place_order'])){    include("../partials/connect.php");    $total =$_POST['total'];    $phone =$_POST['phone'];    $address =$_POST['address'];    $customer_id = $_SESSION['customer_id'];    $sql = "INSERT INTO orders(customer_id, address, phone, total)VALUES('$customer_id', '$address', '$phone', '$total')";    $connection->query($sql);    $sql2 = "SELECT id FROM orders order by id DESC LIMIT 1";    $results = $connection->query($sql2);    $final = $results->fetch_assoc();    $order_id = $final['id'];    foreach ($_SESSION['cart'] as $key => $value){        $product_id = $value['item_id'];        $quantity = $value['quantity'];        $sql3 = "INSERT INTO order_details(order_id, product_id, quantity)VALUES('$order_id', '$product_id', '$quantity')";        $connection->query($sql3);    }}